let Id = global.get("personGroup.Id");

msg.payload = {
    "faceIds":[msg.payload[0].faceId],
    "personGroupId":Id,
    "maxNumOfCandidatesReturned":10,
    "confidenceThreshold":0.5
};

return msg;

var key = msg.payload;
var out = context.get("code") || "";

if (key === "#") {
    key = ""; // send code
}
else if (key === "*") {
    key = ""; // reset code
    out = "";
}
else if (!isNaN(+key)) {
    out += key; // append key
}
context.set("code", key ? out : undefined);

// output #1: keys entered
// output #2: completed code
msg.payload = out;
return [
    key ? msg : {payload: ""},
    out ? msg : null
];

var obj = global.get("personId")||{};
var topic = msg.topic;
obj[topic] = msg.payload.personId;
global.set("personId",obj);
return msg;

let id = "personId."+msg.payload.id;
msg.personId = global.get(id);

msg.payload = {
    "url": msg.payload.url_value
};
return msg;

msg.payload = [
    {
        "Topic": "recognitionModel",
        "Value": msg.payload.recognitionModel
    },
    {
        "Topic": "personGroupId",
        "Value": msg.payload.personGroupId
    },
    {
        "Topic": "name",
        "Value": msg.payload.name
    }
];

return msg;

msg.options = [];
for(var l=0;l<msg.payload.length;l++){
    msg.options.push(l+"");
}
flow.set("data", msg.payload);

return msg;

if(typeof msg.payload === 'string'){
    var n = parseInt(msg.payload);
    var data = flow.get('data');
    msg.payload = [
        {
            "Topic": "personId",
            "Value": data[n].personId
        },
        {
            "Topic": "persistedFaceIds",
            "Value": data[n].persistedFaceIds.toString()
        },
        {
            "Topic": "name",
            "Value": data[n].name
        }
    ];
}

return msg;

var msg2 = {payload: false};
var obj = global.get("personId");
msg.keyNames = Object.keys(obj);
var count=0;

for(var i=0; i<msg.keyNames.length; i++){
    var a = msg.keyNames[i];
    if(msg.payload[0].candidates[0].personId === obj[a]){
        count += 1;
    }
}
if(count>0){
    return [msg, null];
}else{
    return [null, msg2];
}
return msg;
