func:
var key = msg.payload;
var out = context.get("code") || "";

if (key === "#") {
    key = ""; // send code
}
else if (key === "*") {
    key = ""; // reset code
    out = "";
}
else if (!isNaN(+key)) {
    out += key; // append key
}
context.set("code", key ? out : undefined);

// output #1: keys entered
// output #2: completed code
msg.payload = out;
return [
    key ? msg : {payload: ""},
    out ? msg : null
];
